package com.syntax.class07;

public class IncrementAndDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=10;
		x=x+1;
		
		x+=1;
		
		System.out.println(x);
		
		x++;
		System.out.println(x);
		
		x--;
			System.out.println(x);
			
			int num=100;
		num++;
		System.out.println(num);
		
	}

}
