package com.syntax.class07;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int time=13;
		if(time<12 ) {
			System.out.println("Morning");
		}
		System.out.println("--------------------------------------------");
		
		while(time<12 ) {
			System.out.println("Moring");
			time++;
		}
		System.out.println("Hello");
	}

}
