package com.syntax.class07;

public class WhileLoopExamples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//print numbers from 1 to 10;
		int a=100;
				while(a>=50) {
					System.out.print(a+" ");
					a--;
				}
				
				//Print numbers from-1 to-10
				System.out.println();
                int b=1;
                while(b>=-10) {
	             System.out.print(b+" ");
	             b--;
}
                //Print numer from 1 to 20 but only even numbers
                System.out.println();
                int c=0;
                while (c<=20);
                System.out.print(c+" ");
                c=c+2;
                
	}

}
