package com.syntax.class08;

public class ForLoopExamples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for (int i=1; i<=90; i++) {
	System.out.print(i+" ");
}
for(int i=60; i>=10; i--) {
	System.out.println(i+" ");
}
System.out.println();

for(int i=5; i<=40;i+=5) {
	System.out.print(i+" ");
}






	}

}
